package codes.shiftmc.minecraft.server.packet.server.status;

import codes.shiftmc.minecraft.mappings.Motd;
import codes.shiftmc.minecraft.server.packet.server.ServerPacket;
import io.netty.buffer.ByteBuf;

import java.util.List;

import static codes.shiftmc.minecraft.util.ProtocolHelper.writeString;

public record ResponsePacket() implements ServerPacket {

    private static final Motd MOTD = new Motd(
            new Motd.Version(
                    "1.21.5",
                    770
            ),
            new Motd.Players(
                    100,
                    5,
                    List.of(new Motd.Player(
                            "Shift_Sad",
                            "0b1e4937-a79d-45c2-8d12-25c210e4080d"
                    ))
            ),
            new Motd.Description("Hello, world!"),
            "data:image/png;base64,",
            false
    );

    @Override
    public void serialize(ByteBuf buf) {
        writeString(buf, MOTD.toJson());
    }

    @Override
    public int getId() {
        return 0x00;
    }
}
